import { useState } from "react";
import { Rings } from 'react-loader-spinner';

const apiStatusConstants = {
  initial: "INITIAL",
  inProgress: "IN_PROGRESS",
  success: "SUCCESS",
  failure: "FAILURE"
};

const BusinessDashboard = () => {
  const [businessName, setBusinessName] = useState("");
  const [locationName, setLocationName] = useState("");
  const [businessData, setBusinessData] = useState(null);
  const [apiStatus, setApiStatus] = useState(apiStatusConstants.initial);

  const onChangeBusinessName = (event) => setBusinessName(event.target.value);
  const onChangeLocationName = (event) => setLocationName(event.target.value);

  const getBusinessData = async (event) => {
    event.preventDefault();
    setApiStatus(apiStatusConstants.inProgress);

    const apiUrl = "https://backend-phi-one-46.vercel.app/business-data/";
    const options = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: businessName, location: locationName })
    };

    try {
      const response = await fetch(apiUrl, options);
      if (response.ok) {
        const fetchedData = await response.json();
        setBusinessData(fetchedData);
        setApiStatus(apiStatusConstants.success);
      } else {
        setApiStatus(apiStatusConstants.failure);
      }
    } catch {
      setApiStatus(apiStatusConstants.failure);
    }
  };

  const onFailureView = () => (
    <div className="h-screen bg-cover bg-[#F9EBDE] flex flex-col justify-center items-center text-center">
      <h1 className="text-xl font-bold text-[#815854] mb-2">Something went wrong</h1>
      <h1 className="text-lg text-[#815854]">Try again</h1>
    </div>
  );

  const onInProgressView = () => (
    <div className="h-screen bg-cover bg-[#F9EBDE] flex items-center justify-center">
      <Rings
        height={80}
        width={80}
        color="#815854"
        radius="10"
        visible={true}
        ariaLabel="rings-loading"
      />
    </div>
  );

  const onSuccessView = () => (
    <div className="h-screen bg-cover bg-[#F9EBDE] text-center">
      <h1 className="text-[#815854] font-bold text-[28px] p-[20px]">Business Dashboard</h1>
      <form onSubmit={getBusinessData}>
        <div className="flex flex-col items-center justify-center gap-4 p-4">
          <input onChange={onChangeBusinessName} type="text" placeholder="Enter Business Name" className="p-2 rounded-md border border-gray-300 w-1/2" />
          <input onChange={onChangeLocationName} type="text" placeholder="Enter Location Name" className="p-2 rounded-md border border-gray-300 w-1/2" />
        </div>
        <button type="submit" className="bg-[#815854] h-[35px] w-[100px] text-[#F9EBDE] rounded-md border">Submit</button>
      </form>

      {businessData && (
        <div className="mt-6 text-[#815854]">
          <p><strong>Headline:</strong> {businessData.headline}</p>
          <p><strong>Rating:</strong> {businessData.rating}</p>
          <p><strong>Reviews:</strong> {businessData.reviews}</p>
        </div>
      )}
    </div>
  );

  switch (apiStatus) {
    case apiStatusConstants.success:
      return onSuccessView();
    case apiStatusConstants.inProgress:
      return onInProgressView();
    case apiStatusConstants.failure:
      return onFailureView();
    default:
      return onSuccessView();
  }
};

export default BusinessDashboard;
